// lib/connector/index.php
class Response{ public $result; public $affected; public $ind; public $error; public $errorData; function __construct($data, $error) { $this->error = !$error; if (!$error) { $this->errorData = $data->errorInfo(); } else { $this->result = $data->fetchAll(); $this->affected = $data->rowCount(); } $this->ind = 0; $data->closeCursor(); } function error() { return $this->error ? $this->errorData : false; } function getData() { return $this->result; } function getAffected() { return $this->affected; } function next() { return $this->result[$this->ind++]; } function reset() { $this->ind = 0; }}class Connector{ public $queries = array(); public $db; public $log = array(); public $dev = false; function __construct($dsn, $user, $pass) { $this->db = new \PDO($dsn, $user, $pass); $this->log = array(); } function query($query,$obj = null) { $q = $this->db->prepare($query); if ($obj) $e = $q->execute($obj); else $e = $q->execute(); if ($this->dev) array_push($this->log, array( $query, $obj )); return new Response($q,$e); } function _query($sql, $values, $insert, $typeString) { if (isset($this->queries[$sql . "|" . $typeString])) {  $s = $this->queries[$sql . "|" . $typeString]; $q = $s[1]; $v = &$s[0]; foreach ($values as $key => $vq) { $v[$key][0] = $vq[0]; } if ($this->dev) array_push($this->log, array( "fromcache", $sql, $typeString, $values, $insert )); } else { $q = $this->db->prepare($sql); $v = $values; foreach ($v as $key => &$va) { $q->bindParam($key + 1, $va[0],$va[1]); } $this->queries[$sql . "|" . $typeString] = array(&$v,$q); if ($this->dev) array_push($this->log, array( $sql, $typeString, $values, $insert )); } if (count($insert) == 0) {  $e = $q->execute(); return new Response($q, $e); } else {  $responses = array(); $e = $q->execute(); array_push($responses,new Response($q, $e)); foreach ($insert as $key => $value) { foreach ($value as $k => $val) { $v[$k][0] = $val; } $e = $q->execute(); array_push($responses, new Response($q, $e)); } return $responses; } } function close() { $this->db = null; $this->queries = null; } function clearCache() { $this->queries = array(); }}
// lib/parser/Advanced.php
class AdvancedParser{ private static function parseArg(&$str) { if (substr($str, 0, 1) == "[" && substr($str,3,1) == "]") { $out = substr($str, 1, 2); $str = substr($str, 4); return $out; } else { return false; } } private static function append(&$args, $val, $index) { if (gettype($val) == "array") { $len = count($val); for ($k = 1; $k < $len; $k++) { if (!isset($args[$k])) $args[$k] = array(); $args[$k][$index] = $val[$k]; } } } private static function append2(&$insert, $indexes, $dt) { function stripArgs(&$key) { if (substr($key,-1) == "]") { $b = strrpos($key, "[", -1); $key = substr($key,0,$b); } $b = strrpos($key, "]", -1); if ($b !== false) $key = substr($key, $b + 1); } function recurse(&$holder, $val, $indexes, $par) { foreach ($val as $k => $v) { if (gettype($v) == "array") { $a = substr($k, 0, 4); stripArgs($k); if ($a != "[||]" && $a != "[&&]") { if (isset($indexes[$k . "#" . $par . "*"])) $d = $indexes[$k . "#" . $par . "*"]; else $d = $indexes[$k . "*"]; foreach ($v as $i => $j) { $holder[$d + $i] = $j; } } else { recurse($holder, $v, $indexes, $par . "/" . $k); } } else { stripArgs($k); if (isset($indexes[$k . "#" . $par])) $d = $indexes[$k . "#" . $par]; else $d = $indexes[$k]; $holder[$d] = $v; } } } $len = count($dt); for ($key = 1; $key < $len; $key++) { $val = $dt[$key]; if (!isset($insert[$key])) $insert[$key] = array(); recurse($insert[$key], $val, $indexes, ""); } } private static function quote($str) { $str = explode(".", $str); $out = ""; for ($i = 0; $i < count($str); $i++) { if ($i != 0) $out .= "."; $out .= "`" . $str[$i] . "`"; } return $out; } private static function table($table) { if (gettype($table) == "array") { $sql = ""; for ($i = 0; $i < count($table); $i++) { if ($i != 0) $sql .= ", "; $sql .= self::quote($table[$i]); } return $sql; } else { return self::quote($table); } } private static function value($type,$value,&$typeString) { $var = strtolower($type); if (!$var) $var = strtolower(gettype($value)); $type = \PDO::PARAM_INT; if ($var == "boolean" || $var == "bool") { $type = \PDO::PARAM_BOOL; $value = $value ? "1" : "0"; $typeString .= "b"; } else if ($var == "integer" || $var == "int") { $typeString .= "i"; } else if ($var == "string" || $var == "str") { $type = \PDO::PARAM_STR; $typeString .= "s"; } else if ($var == "double" || $var == "doub") { $value = (int) $value; $typeString .= "i"; } else if ($var == "resource" || $var == "lob") { $type = \PDO::PARAM_LOB; $typeString .= "l"; } else if ($var == "null") { $type = \PDO::PARAM_NULL; $value = null; $typeString .= "n"; } return array($value,$type); } private static function getType(&$str) { if (substr($str,-1) == "]") { $start = strpos($str, "["); if ($start === false) { return ""; } $out = substr($str, $start + 1); $str = substr($str,0,$start); return $out; } else return ""; } private static function conditions($dt, &$values = false, &$map = false, &$typeString = "", &$index = 0) {  $build = function(&$build, $dt, &$map, &$index, &$values, &$typeString, $join = " AND ", $operator = " = ", $parent = "") { $num = 0; $sql = ""; foreach ($dt as $key => $val) { $arg = self::parseArg($key); $arg2 = $arg ? self::parseArg($key) : false; $valType = gettype($val); $useBind = !isset($val[0]); $newJoin = $join; $newOperator = $operator; switch ($arg) { case "||": $arg = $arg2; $newJoin = " OR "; break; case "&&": $arg = $arg2; $newJoin = " AND "; break; } switch ($arg) {  case ">>": $newOperator = " > "; break; case "<<": $newOperator = " < "; break; case ">=": $newOperator = " >= "; break; case "<=": $newOperator = " <= "; break; default: if (!$useBind) $newOperator = " = ";  break; } if ($num != 0) $sql .= $join; if ($valType == "array") { if ($useBind) { $sql .= "(" . $build($build,$val,$map,$index,$values, $newJoin, $newOperator, $parent . "/" . $key) . ")";  } else { $type = self::getType($key); if ($map !== false) { $map[$key . "*"] = $index; $map[$key . "#" . $parent . "*"] = $index++; } foreach ($value as $k => $v) { if ($k != 0) $sql .= $newJoin; $sql .= "`" . $key . "`" . $newOperator; $index++; if ($values !== false) { $sql .= "?"; array_push($values,self::value($type,$v,$typeString)); } else { if (gettype($v) == "integer") { $sql .= $v; } else { $sql .= self::quote($v);  } } } } } else { if ($values !== false) { $sql .= "`" . $key . "`" .$newOperator . "?"; array_push($values,self::value(self::getType($key),$val, $typeString)); } else { $sql .= self::quote($key) .$newOperator; if (gettype($val) == "integer") { $sql .= $val; } else { $sql .= self::quote($val);  } } if ($map !== false) { $map[$key] = $index; $map[$key . "#" . $parent] = $index++; } } return $sql; } $num++; }; return $build($build,$dt,$map,$index,$values,$typeString); } static function SELECT($table, $columns, $where, $join, $limit) { $sql = "SELECT "; $len = count($columns); $values = array(); $insert = array(); if ($len == 0) {  $sql .= "*"; } else {  $i = 0; $req = 0; $into = ""; if ($columns[0] == "DISTINCT") { $i = 1; $req = 1; $sql .= "DISTINCT "; } else if (substr($columns[0], 0, 11) == "INSERT INTO") { $i = 1; $req = 1; $sql = $columns[0] . " " . $sql; } else if (substr($columns[0], 0, 4) == "INTO") { $i = 1; $req = 1; $into = " " . $columns[0] . " "; } if ($len > $req) {  for (; $i < $len; $i++) { if ($i > $req) { $sql .= ", "; } $sql .= self::quote($columns[$i]); } } else $sql .= "*"; $sql .= $into; } $sql .= " FROM " . self::table($table); if ($join) { foreach ($join as $key => $val) { $arg = self::parseArg($key); switch ($arg) { case "<<": $sql .= " RIGHT JOIN "; break; case ">>": $sql .= " LEFT JOIN "; break; case "<>": $sql .= " FULL JOIN "; break; default:  $sql .= " JOIN "; break; } $sql .= self::quote($key) . " ON "; $sql .= self::conditions($val); } } $typeString = ""; if (count($where) != 0) { $sql .= " WHERE "; $index = array(); if (isset($where[0])) { $sql .= self::conditions($where[0], $values, $index, $typeString); self::append2($insert,$index,$where); } else { $sql .= self::conditions($where, $values, $index, $typeString); } } if ($limit) $sql .= " LIMIT " . $limit; return array( $sql, $values, $insert, $typeString ); } static function INSERT($table, $data) { $sql = "INSERT INTO " . self::table($table) . " ("; $values = array(); $insert = array(); $typeString = ""; $append = ""; $i = 0; if (isset($data[0])) { $indexes = array(); foreach ($data[0] as $key => $val) { if ($i != 0) { $sql .= ", "; $append .= ", "; } $type = self::getType($key); $sql .= "`" . $key . "`"; $append .= "?"; array_push($values,self::value($type,$val,$typeString)); $indexes[$key] = $i++; } self::append2($insert, $indexes, $data); } else { foreach ($data as $key => $val) { if ($i != 0) { $sql .= ", "; $append .= ", "; } $type = self::getType($key); $sql .= "`" . $key . "`"; $append .= "?"; array_push($values,self::value($type,$val,$typeString)); self::append($insert, $val, $i); $i++; } } $sql .= ") VALUES (" . $append . ")"; return array( $sql, $values, $insert, $typeString ); } static function UPDATE($table, $data, $where) { $sql = "UPDATE " . self::table($table) . " SET "; $values = array(); $insert = array(); $typeString = ""; $i = 0; if (isset($data[0])) { $indexes = array(); foreach ($data[0] as $key => $val) { if ($i != 0) { $sql .= ", "; } $type = self::getType($key); array_push($values,self::value($type,$val,$typeString)); $indexes[$key] = $i++; $sql .= "`" . $key . "` = ?"; } self::append2($insert, $indexes, $data); } else { foreach ($data as $key => $val) { if ($i != 0) { $sql .= ", "; } $type = self::getType($key); array_push($values,self::value($type,$val,$typeString)); $sql .= "`" . $key . "` = ?"; self::append($insert, $val, $i); $i++; } } if (count($where) != 0) { $sql .= " WHERE "; $index = array(); if (isset($where[0])) { $sql .= self::conditions($where[0], $values, $index, $typeString,$i); self::append2($insert,$index,$where); } else { $sql .= self::conditions($where, $values, $index, $typeString,$i); } } return array( $sql, $values, $insert, $typeString ); } static function DELETE($table, $where) { $sql = "DELETE FROM " . self::table($table); $values = array(); $insert = array(); $typeString = ""; if (count($where) != 0) { $sql .= " WHERE "; $index = array(); if (isset($where[0])) { $sql .= self::conditions($where[0], $values, $index, $typeString); self::append2($insert,$index,$where); } else { $sql .= self::conditions($where, $values, $index, $typeString); } } return array( $sql, $values, $insert, $typeString ); }}
// index.phpclass SuperSQL{ public $connector; function __construct($dsn, $user, $pass) { $this->connector = new Connector($dsn, $user, $pass); } function SELECT($table, $columns, $where, $join = null, $limit = false) { if (gettype($join) == "integer") { $limit = $join; $join = null; } $d = AdvancedParser::SELECT($table, $columns, $where, $join, $limit); return $this->connector->_query($d[0], $d[1], $d[2], $d[3]); } function INSERT($table, $data) { $d = AdvancedParser::INSERT($table, $data); return $this->connector->_query($d[0], $d[1], $d[2], $d[3]); } function UPDATE($table, $data, $where) { $d = AdvancedParser::UPDATE($table, $data, $where); return $this->connector->_query($d[0], $d[1], $d[2], $d[3]); } function DELETE($table, $where) { $d = AdvancedParser::DELETE($table, $where); return $this->connector->_query($d[0], $d[1], $d[2], $d[3]); } function query($query, $obj = null) { return $this->connector->query($query, $obj); } function close() { $this->connector->close(); } function dev() { $this->connector->dev = true; } function getLog() { return $this->connector->log; } function clearCache()  { $this->connector->clearCache(); }}
